import  React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { 
  addTask, 
  markTask,
  selectAllTasks 
} from '../allTasks/allTasksSlice.js';

export const Task = ( { task }) => {
  const dispatch = useDispatch();

  const markTask = (task) => {
    dispatch(markTask(task));
  }

  return (
      <div>
        <h4>{task.data} {task.data.isSelected ? 'SELECTED' : ''} </h4>
        <button onClick={() => markTask(task)}>+</button>
      </div>
  );
}
