import  React from 'react';
import { 
  addTask, 
  markTask,
  selectAllTasks 
} from '../allTasks/allTasksSlice.js';

export const Task = ( { task, markTaskHandler }) => {
    return (
        <div>
          <h4>{task.data} {task.data.isSelected ? 'SELECTED' : ''} </h4>
          <button onClick={markTaskHandler(task)}>+</button>
        </div>
    );
}
