import  React,  { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { 
  addTask, 
  markTask,
  selectAllTasks 
} from './allTasksSlice';
import { Task } from '../Task.js/Task';

export const AllTasks = () => {
    const tasks = useSelector(selectAllTasks);
    const dispatch = useDispatch();

    console.log('rendering AllTasks');

    const markTaskHandler = (task) => {
      dispatch(markTask(task));
    }

    return (
        <div>
          <div>
            {tasks.map( task => (
                  <Task key={task.id} task={task} markTaskHandler={markTaskHandler}/>
            ))}
          </div>
          <button onClick={ () => dispatch(addTask({ id: Date.now(), data: Date.now(), isSelected: false })) }>ADD</button>
        </div>
    );
}
