import React from 'react';

import { AllTasks } from '../features/allTasks/AllTasks.js';
import { SingleTaskPage } from '../features/Task.js/SingleTaskPage.js';

export function App() {
  return (
    <Router>
      <Navbar />
      <div className="App">
        <Switch>
          <Route
            exact
            path="/"
            render={() => (
              <React.Fragment>
                <AddPostForm />
                <PostsList />
              </React.Fragment>
            )}
          />
          <Route exact path="/posts/:postId" component={SingleTaskPage} />
          <Redirect to="/" />
        </Switch>
      </div>
    </Router>
  )
}
